Analytics.Script:
CREATE TEMPORARY TABLE memberstatus USING CarbonAnalytics OPTIONS (tableName "MEMBER_LIFECYCLE");
create temporary table member_count using CarbonJDBC options (dataSource "WSO2_ANALYTICS_PROCESSED_DATA_STORE_DB", tableName "MEMBER_COUNT");
;WITH MemberCount as (select application_id, cluster_alias, count(case when member_status='Created' and timestamp > current_time(null)-10000 and timestamp<= current_time(null) then 1 else NULL end) as created_instance_count, count(case when member_status='Initialized' and timestamp > current_time(null)-10000 and timestamp<= current_time(null) then 1 else NULL end) as initialized_instance_count, count(case when member_status='Active' and timestamp > current_time(null)-10000 and timestamp<= current_time(null) then 1 else NULL end) as active_instance_count, count(case when member_status='Terminated' and timestamp > current_time(null)-10000 and timestamp<= current_time(null) then 1 else NULL end) as terminated_instance_count from memberstatus group by application_id, cluster_alias) INSERT INTO table member_count select current_time(null), application_id, cluster_alias, created_instance_count,initialized_instance_count,active_instance_count, terminated_instance_count from MemberCount;
create temporary table member_status_new using CarbonJDBC options (dataSource "WSO2_ANALYTICS_PROCESSED_DATA_STORE_DB", tableName "MEMBER_STATUS");
INSERT OVERWRITE TABLE member_status_new select timestamp, application_id, cluster_alias, member_id, member_status from memberstatus;
create temporary table member_list using CarbonJDBC options (dataSource "WSO2_ANALYTICS_EVENT_STORE_DB", tableName "MEMBER_LIST");
INSERT OVERWRITE TABLE member_list select cluster_id, member_id, member_status from memberstatus;
